INSERT INTO drills (id, name, category, duration, description) VALUES
('4afe6276-87bc-4ddd-9f05-40cd3fd2d26b', 'Dynamic Movement Line', 'Warm-Up', 8, 'Players perform dynamic movements over 15‚Äì20 yards (high knees, butt kicks, skips) to raise core temperature.'),
('157bc68c-70b3-4f16-a0ef-e01fc526d258', 'Rondo Warm-Up 4v1', 'Warm-Up', 8, 'Keep-away in a small grid to activate passing rhythm and scanning.'),
('5168ca67-cef1-4003-8f14-9293dff79a03', 'Tag With Ball', 'Warm-Up', 6, 'Players dribble in a grid while 1‚Äì2 taggers try to tag. Tagged players do a quick task then rejoin.'),
('a4ba9000-07af-41cc-8a6b-8a915a3b5ea2', 'Activation: Hops + Stick', 'Warm-Up', 6, 'Single-leg hops with controlled landings to prep ankles/knees for cutting and contact.'),
('c87e818a-4e5f-4b30-8c70-1044f5f95962', 'Juggling Ladder', 'Warm-Up', 7, 'Players juggle and try to beat personal bests (thigh/foot) to wake up touch and coordination.'),
('5a4d8523-4f8c-4b39-b10a-b194b63e2294', 'Partner Mirror Footwork', 'Warm-Up', 6, 'Pairs face each other; leader moves laterally/forward/back while partner mirrors to prep defensive feet.'),
('92a542d8-52df-4bbe-8adb-2e1fc2cc1c4d', 'Two-Color Reaction Dribble', 'Warm-Up', 7, 'Players dribble freely; on coach call of a color they accelerate to that cone and stop ball under control.'),
('d9af556d-db4a-47d9-823a-391287b151f5', 'Mobility Circuit (Hips/Ankles)', 'Warm-Up', 8, 'Short mobility stations (open gate, lunges, ankle rocks) to improve range of motion.'),
('8291cbf8-e281-43ca-b811-09e14e9e1b92', 'Passing Gates Warm-Up', 'Warm-Up', 8, 'Pairs pass through cone gates and rotate to a new gate after a set number of passes.'),
('2249efca-8002-4249-8d91-2dbeaebad523', '3v1 Keep-Away Warm-Up', 'Warm-Up', 7, 'Small rondo to prime quick decisions and support angles.'),
('ca3d2a9c-cc9b-4959-89d4-ff60d2f601ea', 'Shadow Dribble Stretch', 'Warm-Up', 6, 'Slow dribble while mixing gentle dynamic stretches to loosen up without losing touch.'),
('d7171a04-4f28-4b02-92d4-223fa628e6d0', 'Rondo Warm-Up 5v2', 'Warm-Up', 8, 'Higher-tempo rondo with two defenders to force quicker decisions and cleaner first touches.'),
('958841fd-cde1-4e56-aeca-c50e4ef7ad76', 'Circle Passing + Call Name', 'Warm-Up', 7, 'Players in a circle pass while calling receiver''s name to encourage communication and scanning.'),
('3d9aa179-d7de-42fd-9205-6c93fc4e227e', 'Dribble + Turn Circuit', 'Warm-Up', 8, 'Players dribble between cones performing a different turn at each cone (inside cut, outside cut, pullback).'),
('8f811ae1-4c57-4db1-afe5-9423f10e0307', 'Light Possession 6v3', 'Warm-Up', 9, 'Larger rondo-style possession to activate team spacing and pressing cues.'),
('ad1351b5-a56b-47df-9fa1-cac1ab4be0f9', 'Foundation Touches', 'Ball Mastery (Solo)', 5, 'Alternating inside-foot touches to develop rhythm and comfort on the ball.'),
('cb28868b-bf9d-4366-8d5a-fee5c71cf2af', 'Toe Taps', 'Ball Mastery (Solo)', 4, 'Alternating toe touches on top of the ball to improve balance and quick feet.'),
('d106ebad-a446-4760-b3f0-d2bb833e57a6', 'Bell Touches', 'Ball Mastery (Solo)', 4, 'Tap ball side-to-side with the inside of both feet to build control and rhythm.'),
('c73f8e59-67d7-4343-8fe5-f17f699b9c4a', 'Sole Rolls (Side-to-Side)', 'Ball Mastery (Solo)', 5, 'Roll ball left-right with the sole to improve feel and foot strength.'),
('c4478fcc-a401-43d5-933e-507cfb15e3b6', 'Inside‚ÄìOutside Touches (Same Foot)', 'Ball Mastery (Solo)', 5, 'Using one foot, push ball inside then outside repeatedly, then switch feet.'),
('43c2df66-5ddc-42bd-86d8-1d9075c203aa', 'V-Pulls', 'Ball Mastery (Solo)', 5, 'Pull ball back with sole then push diagonally forward to simulate creating space.'),
('30431a39-fa65-465b-a779-e25b7de5df37', 'Pull-Push Alternating Feet', 'Ball Mastery (Solo)', 5, 'Pull back with one foot and push forward with the other in a rhythm pattern.'),
('91d7f6c5-d1f8-414e-9efc-a583157cac25', 'L-Turn Reps', 'Ball Mastery (Solo)', 5, 'Drag ball back then cut sideways in an L shape to turn away from pressure.'),
('38b49624-6f7a-42d7-b926-0717f1a4804a', 'Cruyff Turn Reps', 'Ball Mastery (Solo)', 5, 'Fake a pass/shot then drag ball behind standing leg to change direction.'),
('42753870-99a6-415f-9257-9197f772a39d', 'Inside Cut + Outside Cut Alternation', 'Ball Mastery (Solo)', 6, 'Dribble to a cone then alternate inside cut and outside cut turns.'),
('11ac71bf-c42d-4a44-a11f-ec6b98064f70', 'Figure-8 Dribble (Two Cones)', 'Ball Mastery (Solo)', 6, 'Dribble a figure-8 pattern around two cones focusing on close control.'),
('aaf47645-429f-4de6-bd74-0cad6fbc1018', 'Box Dribble (Four Cones)', 'Ball Mastery (Solo)', 6, 'Dribble around a square; perform a turn at each corner.'),
('b158521d-2116-4a09-b310-3bfd41853466', 'Ronaldo Chop Reps', 'Ball Mastery (Solo)', 5, 'Outside-of-foot chop across body while hopping to shift weight and change direction.'),
('2b5dc3b2-1272-47ae-975d-6baf1ac3583d', 'Scissors Reps', 'Ball Mastery (Solo)', 5, 'Circular leg movement around ball to sell direction change.'),
('4431cd90-e410-4600-8279-a71957fcec21', 'Step-Over Reps', 'Ball Mastery (Solo)', 5, 'Stepping over the ball to disguise direction before pushing away.'),
('894d846d-110a-4894-ac69-eb7c81913cb3', 'Drag Back (Sole) + Go', 'Ball Mastery (Solo)', 4, 'Pull ball back with sole then push forward to reset and accelerate.'),
('5b8f53a4-434b-4658-8bdf-0dd7e6e7d4b8', 'Outside Touches (Laces) Line', 'Ball Mastery (Solo)', 6, 'Moving forward using outside-of-foot touches to build speed dribbling control.'),
('ca7ff305-941c-45cf-aab6-602940cb3d75', 'Weak-Foot Only Touches', 'Ball Mastery (Solo)', 6, 'Any ball-mastery pattern performed using only weak foot to accelerate development.'),
('a9756874-9423-4315-a559-43ea05957620', 'Sole + Inside Combos', 'Ball Mastery (Solo)', 6, 'Combo touches: sole roll into inside touch repeated in rhythm.'),
('7d41a84a-4ee3-48be-a466-4d663460f605', 'Sole Pull + Outside Push', 'Ball Mastery (Solo)', 6, 'Pull ball back with sole and push away with outside of foot to create separation.'),
('f9ca64a4-a639-4891-ba9b-ba0b99cadc34', 'Tik-Tok Touches (Inside-Outside Alternating Feet)', 'Ball Mastery (Solo)', 6, 'Fast alternating inside-outside touches switching feet each contact to build elite rhythm.'),
('9f1f0057-d15c-4e93-a4b4-214b24d230d3', 'Around-the-World (Coerver) Reps', 'Ball Mastery (Solo)', 5, 'Circling foot around the ball to train dexterity and comfort manipulating the ball.'),
('8e5376b1-0e81-42b8-ad75-435b72c842dd', 'Inside Touches Forward/Back', 'Ball Mastery (Solo)', 5, 'Inside touches moving forward then backward in a lane to sharpen control both directions.'),
('4a7e466c-b7fa-478c-82a4-8d1166c1893a', 'Sole Roll + Pullback Turn', 'Ball Mastery (Solo)', 6, 'Roll ball across body then pull back into turn; repeat both directions.'),
('8d0d4aef-025d-419e-ab5a-f82ef4db6479', 'Touch Box (30-Second Rounds)', 'Ball Mastery (Solo)', 6, 'Inside/sole/outside touches inside a small box for timed rounds.'),
('9555b67e-9387-457f-b961-720c8df49a60', 'Coerver Cuts (Inside Cut Line)', 'Ball Mastery (Solo)', 6, 'Dribble forward and perform inside cuts at each cone to practice sharp direction changes.'),
('2f3f9381-a52d-4d2a-96b5-433f73fbc7ad', 'Coerver Cuts (Outside Cut Line)', 'Ball Mastery (Solo)', 6, 'Dribble forward and perform outside cuts at each cone for outside-foot control.'),
('65136563-97dd-4454-8120-f395b04450d7', 'Maradona Spins (Turn Reps)', 'Ball Mastery (Solo)', 5, 'Use sole to drag ball across body while spinning to turn away from pressure.'),
('a35114dd-6399-4e63-bb53-038248a7b05c', 'Outside-Inside (Zigzag) Lane', 'Ball Mastery (Solo)', 7, 'Zigzag down a lane using outside touch to push, inside to correct line.'),
('cc2d56fe-51c2-4393-8d6e-e5133aa48204', 'Stop-Start Explosions', 'Ball Mastery (Solo)', 6, 'Dribble then stop ball dead, then explode forward on cue to train acceleration control.'),
('bba1e578-1888-4d15-9899-c4dd75539e2d', 'Wall Pass Control (One Touch)', 'First Touch', 7, 'Pass against a wall and control with one touch (inside, outside, sole) alternating surfaces.'),
('774ed072-63a7-4280-9697-d8dc1b38db9c', 'Wall Pass ‚Äì Two Touch Rhythm', 'First Touch', 7, 'Pass, control, pass in steady rhythm to build consistent first touch and passing technique.'),
('542181eb-fca3-4129-bb6c-c3f170252ff5', 'Receive Across Body (Pairs)', 'First Touch', 8, 'Receive pass across body to open up, then play to next cone/target.'),
('25d47d0e-66c2-4efe-918a-aed7c49adf0f', 'Half-Turn Receiving Line', 'First Touch', 10, 'Players receive with back to pressure, check shoulder, half-turn on first touch and accelerate.'),
('7f18358c-dd48-41a8-903f-aa6d980807c7', 'Aerial Control (Toss + Set)', 'First Touch', 8, 'Toss ball to self or partner; control with thigh/chest then settle to feet and play.'),
('520a2296-2c16-4224-89aa-c2ecc16360e8', 'Receive + 2-Touch Direction Change', 'First Touch', 10, 'Receive, take first touch across body, second touch pass to next player in new direction.'),
('a763edbd-4cf0-4d72-8fbd-76ecb3c12bf9', 'First Touch Into Gate', 'First Touch', 8, 'Player receives pass and first touch must enter a cone gate before next action.'),
('07d626b6-9c9e-4d2c-b436-61530b770674', 'Bounce Touch ‚Äì One Bounce Control', 'First Touch', 6, 'Drop ball, let it bounce once, then control with foot to stop or move into space.'),
('c5ccf9d8-3800-4cfa-a4ae-e26ebe7f6cff', 'Receive Under Pressure (Shadow)', 'First Touch', 10, 'Receiver gets tight shadow pressure and must take first touch away and play out.'),
('5080544f-ac93-4378-b050-efc49726ae47', 'Turn & Receive (Check Away, Check To)', 'First Touch', 10, 'Player checks away from cone then checks to receive; first touch sets up next action.'),
('1bbd602f-679b-45e2-9857-1f72b0244664', 'Receive on Back Foot (Open Touch)', 'First Touch', 8, 'Receive with back foot to play forward quickly.'),
('493c497f-d281-44d5-aa4e-ea54416e53de', 'Wall Receive Half-Turn', 'First Touch', 8, 'Pass to wall, receive on half-turn, accelerate two touches, repeat.'),
('8c94e4bd-d5d4-4f90-823c-b07fbfb128be', 'First Touch + Finish (Rebounder/Wall)', 'First Touch', 10, 'Use wall/rebounder as passer; take first touch out of feet and finish into goal.'),
('32133583-b4a6-4db0-bddf-aebc28bc2dde', 'Receive & Switch (Long Touch Prep)', 'First Touch', 10, 'Receive then play longer pass to switch point; focus on first touch to set passing angle.'),
('ad506c91-bdbd-41ad-bf22-b2fd4058609e', 'Pressure Touch Escape (3-Second Rule)', 'First Touch', 8, 'Player must receive and exit box within 3 seconds to simulate fast decision windows.'),
('c9ebf94a-a74e-4d1f-906c-ad9a968181c5', 'Cone Slalom Dribble', 'Dribbling & 1v1', 8, 'Dribble through cones focusing on close control and quick turns.'),
('f15a520b-63fa-4407-9b2b-799039cb4d51', 'Speed Dribble (Straight Line)', 'Dribbling & 1v1', 6, 'Dribble at speed in a lane with longer touches while keeping ball under control.'),
('f3265e3a-416b-44bb-aaaa-e29da55b55bb', 'Change of Pace Dribble', 'Dribbling & 1v1', 7, 'Alternate slow control with explosive acceleration to beat defenders.'),
('c2be784a-3b56-40cc-bcc8-e506a800164b', '1v1 Gate Attack', 'Dribbling & 1v1', 12, 'Attacker tries to dribble through a cone gate while defender pressures and blocks.'),
('65146beb-3c60-4ed4-a5a0-7bd7b0d4bade', '1v1 Endline to Goal', 'Dribbling & 1v1', 12, 'Attacker starts wide and drives to goal; defender tries to delay and win ball.'),
('56543077-8609-40dc-b79a-42f8552de284', '2v1 Transition (Small Goal)', 'Dribbling & 1v1', 12, 'Two attackers vs one defender; focus on dribble to commit then pass or finish.'),
('3881fab2-1731-4aa1-afa7-82c6c5465fd2', 'Shield & Spin (Pressure Cone)', 'Dribbling & 1v1', 8, 'Player shields from imaginary pressure then spins out with first touch.'),
